package automationtesting;

public class UpdateValueofLocalVariable {

	static void Age()
	{
		int ageofmother;
		ageofmother=40;
		ageofmother=50;
		System.out.println(ageofmother);
		
	}
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int age;// declaration
		
		age=27;//Initialization
		age=21;//UPDATE
		
		String name="treesa";
		name="Hepzi";//update
		System.out.println(name);
		
		System.out.println(age);
		Age();

	}

}
